SpawnManager = {};
local initialSpawn = true;

AddEventHandler('playerSpawned', function(spawn)
    Citizen.Wait(3000);
    if initialSpawn == true then
        SpawnManager.InitializePlayer()
    end
end)

RegisterCommand('spawn', function(source, args, rawCommand)
    SpawnManager.InitializePlayer()
end)

function SpawnManager.InitializePlayer()
    initialSpawn = false;
    playerPed = GetPlayerPed(-1);
    SetEntityCoords(playerPed, 459.27743530273, -992.36853027344, 30.689586639404, 1, 0, 59.749336242676, 1);
