    SetRelationshipBetweenGroups(0, GetHashKey('FIREMAN'), GetHashKey("PLAYER"));
    SetRelationshipBetweenGroups(0, GetHashKey('ARMY'), GetHashKey("PLAYER"));
    SetRelationshipBetweenGroups(0, GetHashKey('MEDIC'), GetHashKey("PLAYER"));
